    protected override async Task OnInitializedAsync()
    {
        llgs = (await LecturerLecturerGroupService.GetLecturerLecturerGroups());
        lgs = (await LecturerGroupService.GetLecturerGroups());
        ls = (await LecturerService.GetLecturers());
    }

